import { Map, fromJS } from "immutable";
import { expect } from "chai";
import application from "../reducers/application";

describe("reducer", () => {
  it("handles SET_APPLICATION", () => {
    const initialState = Map();
    const action = {type:"SET_APPLICATION", value: fromJS({
        application:"ghost"})
    };
    const nextState = application(initialState, action);

    expect(nextState).to.equal(fromJS({
      application_name:"ghost"}));
  });
  it("handles SET_APPLICATION_ONE_CLICK_APP when exist SSHKeys", () => {
    const initialState = Map();
    const action = {
      type:"SET_APPLICATION_ONE_CLICK_APP",
      value: fromJS({
        applications: [{
          "id": 0,
          "title": "My little key",
          "provider": "digital_ocean",
          "fingerprint": "00:11:22:33:44:55:66:77:88:99:aa:bb:cc:dd:ee:ff",
          "key": "ssh-rsa  schacon@mylaptop.local"
        },
        {
          "id": 1,
          "name": "My key",
          "provider": "digital_ocean",
          "fingerprint": "00:11:22:33:44:55:66:77:88:99:aa:bb:cc:dd:ee:ff",
          "public_key": "ssh-rsa  schacon@mylaptop.local"
        }],
        application: [{
          "id": 1,
          "name": "My little key",
          "provider": "digital_ocean",
          "fingerprint": "00:11:22:33:44:55:66:77:88:99:aa:bb:cc:dd:ee:ff",
          "public_key": "ssh-rsa  schacon@mylaptop.local"
        }]
      })
    };
    const nextState = application(initialState, action);

    expect(nextState).to.equal(fromJS({
      application_oneclickapp: [{
        "id": 0,
        "title": "My little key",
        "provider": "digital_ocean",
        "fingerprint": "00:11:22:33:44:55:66:77:88:99:aa:bb:cc:dd:ee:ff",
        "key": "ssh-rsa  schacon@mylaptop.local"
      },{
        "id": 1,
        "name": "My little key",
        "provider": "digital_ocean",
        "fingerprint": "00:11:22:33:44:55:66:77:88:99:aa:bb:cc:dd:ee:ff",
        "public_key": "ssh-rsa  schacon@mylaptop.local"
      }]
    }));
  });
  it("handles SET_APPLICATION_ONE_CLICK_APP when no exist SSHKeys", () => {
    const initialState = Map();
    const action = {
      type:"SET_APPLICATION_ONE_CLICK_APP",
      value: fromJS({
        applications: [],
        application: [{
          "id": 1,
          "name": "My little key",
          "provider": "digital_ocean",
          "fingerprint": "00:11:22:33:44:55:66:77:88:99:aa:bb:cc:dd:ee:ff",
          "public_key": "ssh-rsa  schacon@mylaptop.local"
        }]
      })
    };
    const nextState = application(initialState, action);

    expect(nextState).to.equal(fromJS({
      application_oneclickapp: [{
        "id": 1,
        "name": "My little key",
        "provider": "digital_ocean",
        "fingerprint": "00:11:22:33:44:55:66:77:88:99:aa:bb:cc:dd:ee:ff",
        "public_key": "ssh-rsa  schacon@mylaptop.local"
      }]
    }));
  });
  it("handles DEFAULT", () => {
    const initialState = Map();
    const action = {type:""};
    const nextState = application(initialState, action);

    expect(nextState).to.equal(fromJS({
    }));
  });
});
